                return false;
              } else {
                Meteor.call("queryPosImeiInvoiceByImei", imeiInputTem, vm.itemId, function (err, result) {
                  if (result) {
                    vm.$message({
                      type: 'error',
                      message: 'បញ្ចូលរួចម្តងហើយ!!!!!!!'
                    });
                  } else {
                    vm.imei.push({
                      name: imeiInputTem,
                      itemId: vm.itemId
                    });
                    vm.imeiShow.push({
                      name: imeiInputTem,
                      itemId: vm.itemId
                    });
                    imeiInputTem = "";
                    vm.rowDoc.desc = "";
                    vm.imeiShow.forEach(function (o) {
                      vm.rowDoc.desc = vm.rowDoc.desc + " " + o.name;
                    });
                    vm.rowDoc.imei = vm.imeiShow;
                    vm.rowDoc.numImei = vm.imeiShow.length;
                    vm.updatePosInvoiceDetail(vm.rowDoc, vm.indexRow);
                    vm.$message({
                      message: "\u1794\u1789\u17D2\u1785\u17BC\u179B " + imeiInputTem + " \u1794\u17B6\u1793\u1787\u17C4\u1782\u1787\u17D0\u1799",
                      type: 'success'
                    });
                  }
                });
              }
            } else {
              vm.$message({
                type: 'error',
                message: 'មិនមាន Imei នេះទេ!!!!!!!'
              });
            }
          });
        } else {
          var isFindImei = vm.imei.find(function (obj) {
            return obj.name === imeiInputTem && obj.itemId === vm.itemId;
          });

          if (isFindImei) {
            vm.$message({
              type: 'error',
              message: 'បញ្ចូលរួចម្តងហើយ!!!!!!!'
            });
            return false;
          } else {
            Meteor.call("queryPosImeiInvoiceByImei", imeiInputTem, vm.itemId, function (err, result) {
              if (result) {
                vm.$message({
                  type: 'error',
                  message: 'បញ្ចូលរួចម្តងហើយ!!!!!!!'
                });
              } else {
                vm.imei.push({
                  name: imeiInputTem,
                  itemId: vm.itemId
                });
                vm.imeiShow.push({
                  name: imeiInputTem,
                  itemId: vm.itemId
                });
                imeiInputTem = "";
                vm.rowDoc.desc = "";
                vm.imeiShow.forEach(function (o) {
                  vm.rowDoc.desc = vm.rowDoc.desc + " " + o.name;
                });
                vm.rowDoc.imei = vm.imeiShow;
                vm.rowDoc.numImei = vm.imeiShow.length;
                vm.updatePosInvoiceDetail(vm.rowDoc, vm.indexRow);
                vm.$message({
                  message: "\u1794\u1789\u17D2\u1785\u17BC\u179B " + imeiInputTem + " \u1794\u17B6\u1793\u1787\u17C4\u1782\u1787\u17D0\u1799",
                  type: 'success'
                });
              }
            });
          }
        }
      }
    },
    removeImei: function (index, row) {
      var _this13 = this;

      var removeIndex = this.imei.map(function (item) {
        return item.name;
      }).indexOf(row.name);
      this.imei.splice(removeIndex, 1);
      this.imeiShow.splice(index, 1);
      this.rowDoc.desc = "";
      this.imeiShow.forEach(function (o) {
        _this13.rowDoc.desc = _this13.rowDoc.desc + " " + o.name;
      });
      this.rowDoc.numImei = this.imeiShow.length;
      this.updatePosInvoiceDetail(this.rowDoc, this.indexRow);
      this.imeiInput = "";
    },
    popUpAddImei: function (doc, index) {
      this.itemId = doc.itemId;
      this.itemName = doc.itemName;
      this.dialogAddImei = true;
      this.getImeiShow(doc.itemId, index);
      this.indexRow = index;
      this.rowDoc = doc;
    },
    getImeiShow: function (itemId, index) {
      var vm = this;
      this.imeiShow = [];
      this.imei.forEach(function (obj) {
        if (obj.itemId === itemId) {
          vm.imeiShow.push({
            name: obj.name,
            itemId: obj.itemId
          });
        }
      });
    }
  },
  created: function () {
    this.isSearching = true;
    this.queryData();
    this.getTotal();
    this.locationOpt();
    Meteor.subscribe('Pos_InvoiceReact');
    var ma = Manage_Module.findOne();

    if (ma && ma.feature) {
      this.phoneShop = ma.feature.indexOf("Phone Shop") > -1 ? true : false;
      this.validateImei = ma.validateImei;
      this.isMiniInvoice = ma.isMiniInvoice;

      if (this.phoneShop === true) {
        this.numMini = 1;
      }
    }
  },
  computed: {
    langConfig: function () {
      var _this14 = this;

      var data = compoLang.filter(function (config) {
        return config.lang === _this14.langSession;
      })[0]['invoice'];
      return data;
    }
  }
});

