    import draggable from 'vuedraggable';
    import {formatCurrency} from "../../../imports/api/methods/roundCurrency";
    import {formatCurrencyLast} from "../../../imports/api/methods/roundCurrency";
    import {GeneralFunction} from "../../../imports/api/methods/generalFunction";
    import {getCurrencySymbolById} from "../../../imports/api/methods/roundCurrency";
    import {WB_waterBillingSetup} from "../../collection/waterBillingSetup";
    import {Manage_Module} from "../../collection/manageModule";
    // require('cleave.js/dist/addons/cleave-phone.ac');
    // require('cleave.js/dist/addons/cleave-phone.{country}');
    import compoLang from '../../../both/i18n/lang/elem-label'
    import {Pos_InvoiceReact} from "../../collection/posInvoice";

    export default {
        meteor: {
            langSession() {
                return Session.get('lang') || "en";
            },
            disabledRemove() {
                return Session.get("canRemove");
            },
            disabledUpdate() {
                return Session.get("canUpdate");
            },
            newRe() {
                let vm = this;
                Pos_InvoiceReact.find({}).fetch();
                vm.queryData(vm.searchData, vm.skip, vm.currentSize + vm.skip);
            }
        },
        components: {
            draggable
        },
        data() {
            return {
                newLoading: true,
                keyCode: [],
                refForm: '',
                posInvoiceData: [],
                posInvoiceDataDisplay: [],
                posInvoiceShowData: {},
                numMini: 0,
                multipleSelection: [],
                posInvoiceId: "",
                loading: false,
                searchData: '',
                isSearching: false,
                currentPage: 1,
                currentSize: 10,
                count: 0,
                dialogAddPosInvoice: false,
                dialogUpdatePosInvoice: false,
                dialogShowPosInvoice: false,
                dialogAddPosReceiveItem: false,
                dialogAddImei: false,
                typeDiscount: "",
                fullScreen: true,
                phoneShop: false,
                validateImei: false,
                isMiniInvoice: false,
                posInvoiceForm: {
                    itemId: "",
                    itemName: "",
                    price: 0,
                    qty: 0,
                    amount: 0,
                    total: 0,
                    netTotal: 0,
                    balanceUnPaid: 0,
                    paid: 0,
                    invoiceDate: moment().toDate(),
                    dueDate: moment().add(1, "month").toDate(),
                    invoiceNo: "",

                    note: "",

                    discountType: "Amount",
                    discount: 0,
                    discountValue: 0,
                    termId: "",
                    address: "",
                    customerId: "001",

                    isRetail: true,
                    paidUSD: 0,
                    paidKHR: 0,
                    paidTHB: 0,

                    remainUSD: 0,
                    remainKHR: 0,
                    remainTHB: 0,
                    unit1: 1,
                    unit2: 1,
                    totalUnit: 1,
                    desc: "",
                    locationId: "001",
                    rawQty: 0,
                    isReceiveAll: false,
                    balanceNotCut: 0,
                    balanceNotCutFull: 0,
                    code: "",
                    imei: [],
                    isRetailAll: true

                },
                rules: {
                    invoiceDate: [{
                        type: 'date',
                        required: true,
                        message: 'Please input PosInvoiceDate',
                        trigger: 'blur'
                    }],
                    invoiceNo: [{required: true, type: 'string', message: 'Please input Invoice No', trigger: 'blur'}],
                    discount: [{
                        required: true,
                        type: 'string',
                        message: 'Please input Discount',
                        trigger: 'blur'
                    }],
                    customerId: [{
                        required: true,
                        type: 'string',
                        message: 'Please choose customer',
                        trigger: 'change'
                    }],
                    termId: [{
                        required: true,
                        type: 'string',
                        message: 'Please choose Term',
                        trigger: 'change'
                    }],
                    locationId: [{
                        required: true,
                        type: 'string',
                        message: 'Please choose Location',
                        trigger: 'change'
                    }],

                    // note: [{required: true, type: 'string', message: 'Please input Memo', trigger: 'blur'}],
                },

                // Options
                itemOption: [],
                customerOption: [],
                termOption: [],

                dialog: true,
                notifications: false,
                sound: true,
                widgets: false,
                labelPosition: "top",
                options: {
                    disabledDate(time) {
                        return false;
                    }
                },
                disabledDate: false,
                closeDate: "",
                type: "",
                posInvoiceDetail: {},
                discountTypeOption: [
                    {label: "amount", value: "Amount"},
                    {label: "percent", value: "Percent"}
                ],
                locationOption: [],
                disabledItem: true,
                timeStamp: [],
                takeBarcode: '',
                skip: 0,
                imei: [],
                imeiShow: [],
                imeiInput: "",
                itemId: "",
                itemName: "",
                indexRow: "",
                rowDoc: "",
            }
        },
        mounted() {

            this.$jQuery('body').off();


            let vm = this;
            vm.options = {
                disabledDate(time) {
                    let min = moment(vm.closeDate).add(1, "days").toDate().getTime();
                    return time.getTime() < min;
                }
            };

            let elem = this.$jQuery('el-dialog.dialogInvoice');
            let checkEvent = $._data($('body').get(0), 'events');
            if (checkEvent.keyup.length <= 1) {
                this.$nextTick(() => {
                    this.$jQuery('body').on('keyup', elem, this.barcodeScanInvoice);
                })
            }

        },
        watch: {
            currentSize(val) {
                this.isSearching = true;
                this.skip = (this.currentPage - 1) * val;
                this.queryData(this.searchData, this.skip, val + this.skip);
            },
            currentPage(val) {
                this.isSearching = true;
                this.skip = (val - 1) * this.currentSize;
                this.queryData(this.searchData, this.skip, this.currentSize + this.skip);
            },
            searchData(val) {
                this.isSearching = true;
                this.skip = (this.currentPage - 1) * this.currentSize;
                this.queryData(val, this.skip, this.currentSize + this.skip);
            },
            "posInvoiceForm.invoiceDate"(val) {
                let vm = this;
                if (vm.dialogUpdatePosInvoice === false) {
                    vm.posInvoiceForm.posInvoiceDate = val;
                }
                if (vm.closeDate && vm.closeDate !== "" && vm.closeDate !== undefined) {
                    vm.options = {
                        disabledDate(time) {
