            barcodeScanInvoice(e) {
                let vm = this;
                if ((this.dialogAddPosInvoice === true || this.dialogUpdatePosInvoice === true) && this.dialogAddImei === false && this.posInvoiceForm.code === "") {
                    let scannerSensitivity = 100;
                    let charCode = event.which || event.keyCode;
                    if (charCode !== 13 && charCode !== 16 && charCode !== 18 && charCode !== 17) {
                        this.takeBarcode += String.fromCharCode(charCode);
                    }


                    if (e.keyCode === 107 && !e.ctrlKey && !e.altKey) {
                        e.preventDefault();
                        vm.savePosInvoice(true, e, true);

                    }

                    this.timeStamp.push(Date.now());
                    if (this.timeStamp.length > 1) {
                        if (this.timeStamp[1] - this.timeStamp[0] >= scannerSensitivity) {
                            this.takeBarcode = '';
                            this.timeStamp = [];
                        } else {
                            if (e.keyCode === 13) {
                                this.posInvoiceForm.code = this.takeBarcode;
                                if (this.takeBarcode !== "") {
                                    vm.addToPosInvoiceData(null);
                                }
                                this.timeStamp = [];
                                this.takeBarcode = ''
                            }
                        }
                    }
                } else if ((this.dialogAddPosInvoice === true || this.dialogUpdatePosInvoice === true) && this.dialogAddImei === true && this.imeiInput === "") {
                    let scannerSensitivity = 100;
                    let charCode = event.which || event.keyCode;
                    if (charCode !== 13 && charCode !== 16 && charCode !== 18 && charCode !== 17) {
                        this.takeBarcode += String.fromCharCode(charCode);
                    }
                    this.timeStamp.push(Date.now());
                    if (this.timeStamp.length > 1) {
                        if (this.timeStamp[1] - this.timeStamp[0] >= scannerSensitivity) {
                            this.takeBarcode = '';
                            this.timeStamp = [];
                        } else {
                            if (e.keyCode === 13) {
                                this.imeiInput = this.takeBarcode;
                                if (this.takeBarcode !== "") {
                                    vm.addImei();
                                }
                                this.timeStamp = [];
                                this.takeBarcode = ''
                            }
                        }
                    }
                } else {
                    if (e.keyCode === 107 && !e.ctrlKey && !e.altKey) {
                        e.preventDefault();
                        this.popupPosInvoiceAdd();
                        this.dialogAddPosInvoice = true;
                        this.resetForm();
                    }
                }
            },

