    import compoLang from '../../../both/i18n/lang/elem-label'
    import {Pos_CustomerReact} from "../../collection/loanCustomer";

    export default {
        meteor: {
            langSession() {
                return Session.get('lang') || "en";
            },
            disabledRemove() {
                return Session.get("canRemove");
            },
            disabledUpdate() {
                return Session.get("canUpdate");
            },
            newRe() {
                let vm = this;
                Pos_CustomerReact.find({}).fetch();
                vm.queryData(vm.searchData, vm.skip, vm.currentSize + vm.skip);
            }
        },
        mounted() {
            this.$jQuery('body').off();

        },
        data() {
            return {
                langSession: null,
                loanCustomerData: [],
                loading: false,
                searchData: '',
                isSearching: false,
                currentPage: 1,
                currentSize: 10,
                count: 0,
                dialogAddPosCustomer: false,
                dialogUpdatePosCustomer: false,

                loanCustomerForm: {
                    name: "",
                    khName: "",
                    address: "",
                    email: "",
                    phoneNumber: "",
                    termId: "",
                    openingBalance: "",
                    note: "",
                    _id: ""
                },
                rules: {
                    name: [{required: true, message: 'Please input name', trigger: 'blur'}],
                    termId: [{
                        required: true,
                        type: 'string',
                        message: 'Please choose creditOfficer',
                        trigger: 'change'
                    }],

                },
                termDataOption: [],
                skip: 0
            }
        },
        watch: {
            currentSize(val) {
                this.isSearching = true;
                this.skip = (this.currentPage - 1) * val;
                this.queryData(this.searchData, this.skip, val + this.skip);
            },
            currentPage(val) {
                this.isSearching = true;
                this.skip = (val - 1) * this.currentSize;
                this.queryData(this.searchData, this.skip, this.currentSize + this.skip);
            },
            searchData(val) {
                this.isSearching = true;
                this.skip = (this.currentPage - 1) * this.currentSize;
                this.queryData(val, this.skip, this.currentSize + this.skip);
            }
        },
        methods: {
            handleSizeChange(val) {
                this.currentSize = val;
            },
            handleCurrentChange(val) {
                this.currentPage = val;
            },
            queryData: _.debounce(function (val, skip, limit) {
                Meteor.call('queryPosCustomer', {
                    q: val,
                    filter: this.filter,
                    options: {skip: skip || 0, limit: limit || 10}
                }, (err, result) => {
                    if (!err) {
                        this.loanCustomerData = result.content;
                        this.count = result.countPosCustomer;
                    }
                    this.isSearching = false;
                });
            }, 300),
            loanTermOption() {
                let selector = {};
                Meteor.call('queryPosTermOption', selector, this.loanCustomerForm._id, (err, result) => {
                    this.termDataOption = result;
                })
            },
            savePosCustomer(event) {
                event.preventDefault();

                let vm = this;
                this.$refs["loanCustomerFormAdd"].validate((valid) => {
                    if (valid) {
                        let loanCustomerDoc = {
                            name: vm.loanCustomerForm.name,
                            khName: vm.loanCustomerForm.khName,
                            address: vm.loanCustomerForm.address,
                            email: vm.loanCustomerForm.email,
                            phoneNumber: vm.loanCustomerForm.phoneNumber,
                            termId: vm.loanCustomerForm.termId,
                            openingBalance: vm.loanCustomerForm.openingBalance,
                            note: vm.loanCustomerForm.note,
                            rolesArea: Session.get('area')
                        };

                        Meteor.call("insertPosCustomer", loanCustomerDoc, (err, result) => {
                            if (!err) {
                                vm.$message({
                                    duration: 1000,
                                    message: this.langConfig['saveSuccess'],
                                    type: 'success'
                                });
                                vm.dialogAddPosCustomer = false;

                                vm.$refs["loanCustomerForm"].resetFields();
                            } else {
                                vm.$message({
                                    duration: 1000,
                                    message: err.message,
                                    type: 'error'
                                });
                            }
                        })
                    }
                })

            },
            updatePosCustomer() {
                let vm = this;
                this.$refs["loanCustomerFormUpdate"].validate((valid) => {
                    if (valid) {
                        let loanCustomerDoc = {
                            _id: vm.loanCustomerForm._id,
                            name: vm.loanCustomerForm.name,
                            khName: vm.loanCustomerForm.khName,
                            address: vm.loanCustomerForm.address,
                            email: vm.loanCustomerForm.email,
                            phoneNumber: vm.loanCustomerForm.phoneNumber,
                            termId: vm.loanCustomerForm.termId,
                            openingBalance: vm.loanCustomerForm.openingBalance,
                            note: vm.loanCustomerForm.note,
                            rolesArea: Session.get('area')
                        };

                        Meteor.call("updatePosCustomer", loanCustomerDoc, (err, result) => {
                            if (!err) {
                                vm.$message({
                                    duration: 1000,
                                    message: this.langConfig['updateSuccess'],
                                    type: 'success'
                                });
                                vm.dialogUpdatePosCustomer = false;

                                vm.$refs["loanCustomerForm"].resetFields();
                            } else {
                                vm.$message({
                                    duration: 1000,
                                    message: this.langConfig['updateFail'],
                                    type: 'error'
                                });
                            }
                        })
                    }
                })

            },
            removePosCustomer(index, row, rows) {
                let vm = this;
                if (row.invoiceNumber <= 0) {
                    this.$confirm(this.langConfig['message'], this.langConfig['warning'], {
                        confirmButtonText: 'OK',
                        cancelButtonText: 'Cancel',
                        type: 'warning'
                    }).then(() => {
                        Meteor.call("removePosCustomer", row._id, (err, result) => {
                            if (!err) {
                                rows.splice(index, 1);

                                vm.$message({
                                    message: `${row.name}` + this.langConfig['removeSuccess'],
                                    type: 'success'
                                });


                            } else {
                                vm.$message({
                                    type: 'error',
                                    message: this.langConfig['removeFail']
                                });
                            }

                        })

                    }).catch(() => {
                        this.$message({
                            type: 'info',
                            message: this.langConfig['cancel']
                        });
                    });
                } else {
                    this.$message({
                        type: 'error',
                        message: this.langConfig['messageInovice'] + " " + row.invoiceNumber
                    });
                }


            },
            findPosCustomerById(doc) {
                let vm = this;
                vm.loanCustomerForm = {};

                Meteor.call("queryPosCustomerById", doc.row._id, (err, result) => {
                    if (result) {
                        vm.loanCustomerForm._id = result._id;
                        vm.loanCustomerForm = result;
                    }
                })
            },
            cancel() {
                this.$message({
                    type: 'info',
                    message: this.langConfig['cancel']
                });
            },
            resetForm() {
                this.loanCustomerForm._id = "";
                if (this.$refs["loanCustomerFormAdd"]) {
                    this.$refs["loanCustomerFormAdd"].resetFields();
                }

                if (this.$refs["loanCustomerFormUpdate"]) {
                    this.$refs["loanCustomerFormUpdate"].resetFields();
                }

            }
        },
        created() {
            this.isSearching = true;
            this.loanTermOption();
            this.queryData();
            Meteor.subscribe('Pos_CustomerReact');

        },
        computed: {
            langConfig() {
                let data = compoLang.filter(config => config.lang === this.langSession)[0]['creditOfficer'];
                return data;
            }
        }
    }
