    import PageA4 from '/imports/vue/ui/report/page/PageA4.vue';
    import {GenerateFile} from '/imports/api/mixins/file-saver-fn.js';
    import compoLangReport from '../../../../both/i18n/lang/elem-label-sch-report';

    export default {
        mixins: [GenerateFile],
        mounted() {
            this.$jQuery('body').off();

        },
        data() {
            return {
                params: {
                    branch: '',
                    area: '',
                    date: null,
                    teacherId: "",
                    activityId: ""

                },
                rolesArea: '',
                activeName: '1',
                teacherActivityHtml: "",
                labelPosition: 'top',
                branchOptions: [],
                areaOptions: [],
                teacherOptions: [],
                activityOptions: [],

                waterBillingSetup: {
                    khName: '',
                    enName: ''
                },

                loading: false,
                exportLoading: false,

                checkAll: false,
                isIndeterminate: true,
                dateHeader: "",
                currencyHeader: "",
                pickerDateOptions: {
                    shortcuts: [{
                        text: 'Last week',
                        onClick(picker) {
                            const end = moment().endOf("day").toDate();
                            const start = new Date();
                            start.setTime(start.getTime() - 3600 * 1000 * 24 * 7);
                            picker.$emit('pick', [start, end]);
                        }
                    }, {
                        text: 'Last month',
                        onClick(picker) {
                            const end = moment().add(-1, "month").endOf("month").toDate();
                            const start = moment().add(-1, "month").startOf("month").toDate();
                            picker.$emit('pick', [start, end]);
                        }
                    }, {
                        text: 'Last 3 months',
                        onClick(picker) {
                            const end = moment().add(-1, "month").endOf("month").toDate();
                            const start = moment().add(-4, "month").startOf("month").toDate();
                            picker.$emit('pick', [start, end]);
                        }
                    }, {
                        text: 'This month',
                        onClick(picker) {
                            const end = moment().endOf("month").toDate();
                            const start = moment().startOf("month").toDate();
                            picker.$emit('pick', [start, end]);
                        }
                    }, {
                        text: 'Today',
                        onClick(picker) {
                            const end = moment().endOf("day").toDate();
                            const start = new Date();
                            picker.$emit('pick', [start, end]);
                        }
                    }]
                },
                groupByOptions: [
                    {label: "None", value: "None"},
                    {label: "Transaction Type", value: "Transaction Type"},
                    {label: "Customer", value: "Customer"},
                    {label: "Item", value: "Item"},
                    {label: "Day", value: "Day"},
                    {label: "Week", value: "Week"},
                    {label: "Month", value: "Month"},
                    {label: "Quarter", value: "Quarter"},
                    {label: "Year", value: "Year"},
                ]
            };
        },
        meteor: {
            rolesArea() {
                return Session.get('area');
            },
            langSessionReport() {
                return Session.get('lang') || "en";
            }
        },
        watch: {

            "params.branch"(val) {
                this.params.area = "";
                this.fetchArea(val);
            },
            "params.programId"(val) {
                if (val !== "") {
                    this.fetchMajor(val);
                } else {
                    this.params.majorId = "";
                }
            },
            "params.majorId"(val) {
                if (val !== "") {
                    this.fetchLevel(val);
                } else {
                    this.params.levelId = "";
                }
            }
        },
        created() {
            Meteor.call('getWaterBillingSetup', Session.get('area'), (err, result) => {
                if (result) {
                    this.waterBillingSetup = result;
                }
            })
            this.fetchBranch();
            this.fetchTeacher();
            this.fetchActivity();
        },
        methods: {

            fetchBranch() {
                Meteor.call("queryRoleBranchOptionReport", (err, result) => {
                    if (result) {
                        this.branchOptions = result;
                    }
                })
            },
            fetchArea(branchList) {
                Meteor.call("queryRoleAreaOptionReport", branchList, (err, result) => {
                    if (result) {
                        this.areaOptions = result;
                    }
                })
            },
            fetchTeacher() {
                let selector = {};
                Meteor.call("queryTeacherOption", selector, (err, result) => {
                    if (result) {
                        this.teacherOptions = result;
                    }
                })
            },
            fetchActivity() {
                let selector = {};
                Meteor.call("queryActivityOption", selector, (err, result) => {
                    if (result) {
                        this.activityOptions = result;
                    }
                })
            },
            handleRun() {
                this.loading = true;

                /*if (this.params.date == "" || this.params.date == undefined) {
                    alertify.error("Date can't not empty!!");
                    this.loading = false;
                    return false;
                }*/
                Meteor.call('schTeacherActivityReport', this.params, this.langConfig, (err, result) => {
                    if (result) {
                        this.teacherActivityHtml = result.teacherActivityHTML;
                        this.dateHeader = result.dateHeader;
                    }
                    this.loading = false;
                });
            },
            onLoadHandler: function () {
                this.onLoad = true;
            }


        },
        computed: {
            dataExist() {
                // return this.posSaleData.length > 0;
            },
            langConfig() {
                let data = compoLangReport.filter(config => config.lang === this.langSessionReport)[0]['teacherActivity'];
                return data;
            }
        },
        components: {
            a4: PageA4
        },
    }
