                        });
                        vm.posBillForm.itemId = "";
                        vm.posBillForm.code = "";
                        vm.$message({
                            duration: 1000,
                            message: `បន្ថែម` + data.code + " : " + data.name + " បានជោគជ័យ !",
                            type: 'success'
                        });

                        this.getTotal();
                    } else {
                        vm.$message({
                            duration: 1000,
                            message: "មិនមានឈ្មេាះនេះឡើយ!!",
