            updateLoanConfig() {
                let vm = this;
                this.$refs["loanConfigFormUpdate"].validate((valid) => {
                    if (valid) {
                        if (vm.thumbImgCroppa && vm.thumbImgCroppa.hasImage() && vm.imgUrl) {
                            const upload = Images.insert({
                                file: vm.imgUrl,
                                streams: 'dynamic',
                                chunkSize: 'dynamic'
                            }, false);

                            upload.on('start', function (error, result) {
                                if (error) {
                                    console.log(error.message);
                                }
                            })

                            upload.on('end', function (error, fileObj) {
                                if (error) {
                                    console.log(error.message);
                                } else {
                                    let imageId = fileObj._id;
                                    let imagePath = fileObj._downloadRoute + "/" + fileObj._collectionName + "/" + fileObj._id + "/original/" + fileObj._id + fileObj.extensionWithDot;

                                    let loanConfigDoc = {
                                        _id: vm.loanConfigForm._id,
                                        code: vm.loanConfigForm.code,
                                        name: vm.loanConfigForm.name,
                                        khName: vm.loanConfigForm.khName,
                                        subConfigOf: vm.loanConfigForm.subConfigOf,
                                        description: vm.loanConfigForm.description,
                                        imageId: imageId,
                                        imagePath: imagePath
                                    };

                                    Meteor.call("updateLoanConfig", loanConfigDoc, (err, result) => {
                                        if (!err) {
                                            vm.$message({
                                                duration: 1000,
                                                message: `
                        Update
                        Successfully
                        !`,
                                                type: 'success'
                                            });
                                            vm.dialogUpdateLoanConfig = false;
                                            vm.parentLoanConfigOption();
                                            vm.$refs["loanConfigFormUpdate"].resetFields();
                                        } else {
                                            vm.$message({
                                                duration: 1000,
                                                message: `
                        Update
                        Failed
                        !`,
                                                type: 'error'
                                            });
                                        }
                                    })

                                }
                            })

                            upload.start();

                        } else {


                            let loanConfigDoc = {
                                _id: vm.loanConfigForm._id,
                                code: vm.loanConfigForm.code,
                                name: vm.loanConfigForm.name,
                                khName: vm.loanConfigForm.khName,
                                subConfigOf: vm.loanConfigForm.subConfigOf,
                                description: vm.loanConfigForm.description,
                            };

                            Meteor.call("updateLoanConfig", loanConfigDoc, (err, result) => {
                                if (!err) {
                                    vm.$message({
                                        duration: 1000,
                                        message: `
                        Update
                        Successfully
                        !`,
                                        type: 'success'
                                    });
