                                        message: `
                        Update
                        Failed
                        !`,
                                        type: 'error'
                                    });
                                }
                            })
                        }
                    }
                })

            },
            removeLoanConfig(index, row, rows) {
                let vm = this;
                this.$confirm('This will permanently delete the file. Continue?', 'Warning', {
                    confirmButtonText: 'OK',
                    cancelButtonText: 'Cancel',
                    type: 'warning'
                }).then(() => {
                    Meteor.call("removeLoanConfig", row._id, row.imageId, (err, result) => {
                        if (!err) {
                            rows.splice(index, 1);

                            vm.$message({
                                message: `
                         ${row.code} : ${row.name} `,
                                type: 'success'
                            });

                            vm.parentLoanConfigOption();
                        } else {
                            vm.$message({
                                type: 'error',
                                message: 'Delete Failed'
                            });
                        }

                    })

                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: 'Delete canceled'
                    });
                });


            },
            findLoanConfigById(doc) {
                let vm = this;
                vm.loanConfigForm = {};

                Meteor.call("queryLoanConfigById", doc.row._id, (err, result) => {
                    if (result) {
                        vm.loanConfigForm._id = result._id;
                        if (result.subConfigOf == undefined || result.subConfigOf == "") {
                            result.subConfigOf = "";
                        }
                        vm.loanConfigForm = result;
                        vm.imgUrlUpdate = result.imagePath || "";
                        this.parentLoanConfigOption();
                    }
                })
            },
            cancel() {
                this.$message({
                    type: 'info',
                    message: 'Canceled'
                });
            },
            resetForm() {
                this.imgUrlUpdate = "";
                this.thumbImgCroppa = null;

                this.loanConfigForm._id = "";
                this.parentLoanConfigOption();
                if (this.$refs["loanConfigFormAdd"]) {
                    this.$refs["loanConfigFormAdd"].resetFields();
                }

                if (this.$refs["loanConfigFormUpdate"]) {
                    this.$refs["loanConfigFormUpdate"].resetFields();
                }

            },
            generateThumbImgUrl: function () {
                this.isloadingImage = true;
                let url = this.thumbImgCroppa && this.thumbImgCroppa.getChosenFile();
                this.isloadingImage = false;
                // let url = this.thumbImgCroppa && this.thumbImgCroppa.generateDataUrl();
                if (!url) {
                    return
                }
                this.imgUrl = url
            },
            handleThumbImgCroppaZoom() {
                this.generateThumbImgUrl();
            },
            handleThumbImgCroppaMove() {
                this.generateThumbImgUrl();
            },
            handleThumbImgRemove() {
                this.imgUrl = null
            },
            handlePictureCardPreview(url) {
                this.dialogImageUrl = url;
                this.dialogVisible = true;
            }
        },
        created() {
            this.isSearching = true;
            this.parentLoanConfigOption();
            this.queryData();
            Meteor.subscribe('Loan_ConfigReact');

        },
        computed: {
            langConfig() {
                let data = compoLang.filter(config => config.lang === this.langSession)[0]['config'];
                return data;
            }
        }
