                dialogImageUrl: "",
                dialogVisible: false,
                isloadingImage: false,
                thumbImgCroppa: null,
                imgUrl: "",
                imgUrlUpdate: "",
                refForm: "",
                posProductData: [],
                loading: false,
                searchData: '',
                isSearching: false,
                currentPage: 1,
                currentSize: 10,
