    import compoLang from '../../../both/i18n/lang/elem-label-loan'
    import {Loan_RepaymentReact} from "../../collection/loanRepayment";

    export default {
        meteor: {
            langSession() {
                return Session.get('lang') || "en";
            },
            disabledRemove() {
                return Session.get("canRemove");
            },
            disabledUpdate() {
                return Session.get("canUpdate");
            },
            newRe() {
                let vm = this;
                Loan_RepaymentReact.find({}).fetch();
                vm.queryData(vm.searchData, vm.skip, vm.currentSize + vm.skip);
            }
        },
        mounted() {
            this.$jQuery('body').off();

        },
        data() {
            return {
                langSession: null,
                loanRepaymentData: [],
                loading: false,
                searchData: '',
                isSearching: false,
                currentPage: 1,
                currentSize: 10,
                count: 0,
                dialogAddLoanRepayment: false,
                dialogUpdateLoanRepayment: false,

                loanRepaymentForm: {
                    clientId: "",
                    productId: "",
                    currencyId: "",
                    loanAmount: 0,
                    projectInterest: "",
                    repaymentDate: "",
                    coId: "",
                    status: "Active",
                    description: "",
                    feeAmount: 0,
                    startPaidDate: "",
                    installment: 0,
                    _id: ""
                },
                rules: {
                    clientId: [{
                        required: true,
                        type: 'string',
                        message: 'Please choose Client',
                        trigger: 'change'
                    }],
                    productId: [{
                        required: true,
                        type: 'string',
                        message: 'Please choose Product',
                        trigger: 'change'
                    }],
                    currencyId: [{
                        required: true,
                        type: 'string',
                        message: 'Please choose Currency',
                        trigger: 'change'
                    }],
                    coId: [{
                        required: true,
                        type: 'string',
                        message: 'Please choose credit officer',
                        trigger: 'change'
                    }],
                    productType: [{
                        required: true,
                        type: 'string',
                        message: 'Please choose Product Type',
                        trigger: 'change'
                    }],
                    loanAmount: [{
                        required: true,
                        message: 'Please input Amount',
                        trigger: 'blur'
                    }],
                    feeAmount: [{
                        required: true,
                        message: 'Please input Fee Amount',
                        trigger: 'blur'
                    }],
                    installment: [{
                        required: true,
                        message: 'Please input installment',
                        trigger: 'blur'
                    }],
                    repaymentDate: [{
                        type: 'date',
                        required: true,
                        message: 'Please input Repayment Date',
                        trigger: 'blur'
                    }],
                    startPaidDate: [{
                        type: 'date',
                        required: true,
                        message: 'Please input Start Paid Date',
                        trigger: 'blur'
                    }],
                },
                clientOption: [],
                currencyOption: [
                    {label: "USD", value: "USD"},
                    {label: "KHR", value: "KHR"},
                    {label: "THB", value: "THB"},
                ],
                productTypeOption: [
                    {label: "Weekly", value: "Weekly"},
                    {label: "Monthly", value: "Monthly"},
                    {label: "Yearly", value: "Yearly"},
                ],
                productOption: [],
                creditOfficerOption: [],
                skip: 0
            }
        },
        watch: {
            currentSize(val) {
                this.isSearching = true;
                this.skip = (this.currentPage - 1) * val;
                this.queryData(this.searchData, this.skip, val + this.skip);
            },
            currentPage(val) {
                this.isSearching = true;
                this.skip = (val - 1) * this.currentSize;
                this.queryData(this.searchData, this.skip, this.currentSize + this.skip);
            },
            searchData(val) {
                this.isSearching = true;
                this.skip = (this.currentPage - 1) * this.currentSize;
                this.queryData(val, this.skip, this.currentSize + this.skip);
            }
        },
        methods: {
            handleSizeChange(val) {
                this.currentSize = val;
            },
            handleCurrentChange(val) {
                this.currentPage = val;
            },
            queryData: _.debounce(function (val, skip, limit) {
                Meteor.call('queryLoanRepayment', {
                    q: val,
                    filter: this.filter,
                    rolesArea:Session.get('area'),
                    options: {skip: skip || 0, limit: limit || 10}
                }, (err, result) => {
                    if (!err) {
                        this.loanRepaymentData = result.content;
                        this.count = result.countLoanRepayment;
                    }
                    this.isSearching = false;
                });
            }, 300),
            removeLoanRepayment(index, row, rows) {
                let vm = this;
                this.$confirm(this.langConfig['message'], this.langConfig['warning'], {
                    confirmButtonText: 'OK',
                    cancelButtonText: 'Cancel',
                    type: 'warning'
                }).then(() => {
                    Meteor.call("removeLoanRepayment", row._id, (err, result) => {
                        if (!err) {
                            rows.splice(index, 1);

                            vm.$message({
                                message: this.langConfig['removeSuccess'],
                                type: 'success'
                            });


                        } else {
                            vm.$message({
                                type: 'error',
                                message: this.langConfig['removeFail']
                            });
                        }

                    })

                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: this.langConfig['cancel']
                    });
                });
            },
            findLoanRepaymentById(doc) {
                let vm = this;
                vm.loanRepaymentForm = {};

                Meteor.call("queryLoanRepaymentById", doc.row._id, (err, result) => {
                    if (result) {
                        vm.loanRepaymentForm._id = result._id;
                        vm.loanRepaymentForm = result;
                    }
                })
            },
            cancel() {
                this.$message({
                    type: 'info',
                    message: this.langConfig['cancel']
                });
            },
            resetForm() {
                this.loanRepaymentForm._id = "";

            }
        },
        created() {
            this.isSearching = true;
            this.queryData();
            Meteor.subscribe('Loan_RepaymentReact');

        },
        computed: {
            langConfig() {
                let data = compoLang.filter(config => config.lang === this.langSession)[0]['repayment'];
                return data;
            }
        }
    }
