    import compoLang from '../../../both/i18n/lang/elem-label-sch'

    export default {
        meteor: {
            langSession() {
                return Session.get('lang') || "en";
            },
            disabledRemove() {
                return Session.get("canRemove");
            },
            disabledUpdate() {
                return Session.get("canUpdate");
            },
            newRe() {
                let vm = this;
            }
        },
        mounted() {
            this.$jQuery('body').off();

        },
        data() {
            return {
                schInputScoreData: [],
                loading: false,
                searchData: '',
                labelPosition: "top",
                isSearching: false,
                currentPage: 1,
                currentSize: 10,
                count: 0,
                dialogAddSchInputScore: false,
                dialogUpdateSchInputScore: false,
                programList: [],
                facultyList: [],
                subjectOptions: [],
                programOptions: [],
                majorOptions: [],
                yearOptions: [
                    {label: "1", value: 1},
                    {label: "2", value: 2},
                    {label: "3", value: 3},
                    {label: "4", value: 4},
                ],
                semesterOptions: [
                    {label: "1", value: 1},
                    {label: "2", value: 2}
                ],

                params: {
                    programId: "",
                    majorId: "",
                    year: "",
                    generation: 1,
                    subjectId: "",
                    semester: "",

                },
                schInputScoreForm: {
                    name: "",
                    khName: "",
                    code: "",
                    desc: "",
                    _id: "",
                    programId: "",
                    facultyId: "",
                    degree: ""
                },
                rules: {
                    name: [{required: true, message: 'Please input name', trigger: 'blur'}],
                    programId:
                        [{
                            required: true,
                            type: "string",
                            message: 'Please choose program',
                            trigger: 'change'
                        }],
                    facultyId:
                        [{
                            required: true,
                            type: "string",
                            message: 'Please choose faculty',
                            trigger: 'change'
                        }],
                    //code: [{required: true, message: 'Please input code', trigger: 'blur'}]
                },
                skip: 0
            }
        },
        watch: {
            currentSize(val) {
                this.isSearching = true;
                this.skip = (this.currentPage - 1) * val;
                this.handleRun(this.searchData, this.skip, val + this.skip);
            },
            currentPage(val) {
                this.isSearching = true;
                this.skip = (val - 1) * this.currentSize;
                this.handleRun(this.searchData, this.skip, this.currentSize + this.skip);
            },
            searchData(val) {
                this.isSearching = true;
                this.skip = (this.currentPage - 1) * this.currentSize;
                this.handleRun(val, this.skip, this.currentSize + this.skip);
            },
            "params.programId"(val) {
                if (val !== "") {
                    this.fetchMajor(val);
                } else {
                    this.params.majorId = "";
                }
            }
        },
        methods: {
            handleSizeChange(val) {
                this.currentSize = val;
            },
            handleCurrentChange(val) {
                this.currentPage = val;
            },
            handleRun: _.debounce(function (val, skip, limit) {
                Meteor.call('querySchInputScore', {
                    q: val,
                    params: this.params,
                    filter: this.filter,
                    options: {skip: skip || 0, limit: limit || 10}
                }, (err, result) => {
                    if (!err) {
                        this.schInputScoreData = result.content;
                        this.count = result.countSchInputScore;
                    }
                    this.isSearching = false;
                });
            }, 300),
            fetchProgram() {
                let selector = {};
                Meteor.call("queryProgramOption", selector, (err, result) => {
                    if (result) {
                        this.programOptions = result;
                    }
                })
            },
            fetchMajor(programId) {
                let selector = {};
                selector.programId = programId;
                Meteor.call("queryMajorOption", selector, (err, result) => {
                    if (result) {
                        this.majorOptions = result;
                    }
                })
            },
            fetchSubject(query) {

                if (!!query) {
                    setTimeout(() => {
                        Meteor.call('querySubjectOptionLimit', query, (err, result) => {
                            if (!err) {
                                this.subjectOptions = result;
                            } else {
                                console.log(err.message);
                            }
                        })
                    }, 200);
                } else {
                    Meteor.call('querySubjectOptionLimit', "", (err, result) => {
                        if (!err) {
                            this.subjectOptions = result;
                        } else {
                            console.log(err.message);
                        }
                    })
                }
            },
            fetchUser() {
                Meteor.call("queryUserOption", (err, result) => {
                    this.applyUserOption = result;
                })
            },
            saveSchInputScore(event) {
                event.preventDefault();

                let vm = this;
                this.$refs["schInputScoreFormAdd"].validate((valid) => {
                    if (valid) {
                        let schInputScoreDoc = {
                            code: vm.schInputScoreForm.code,
                            name: vm.schInputScoreForm.name,
                            khName: vm.schInputScoreForm.khName,
                            desc: vm.schInputScoreForm.desc,
                            programId: vm.schInputScoreForm.programId,
                            facultyId: vm.schInputScoreForm.facultyId,
                            degree: vm.schInputScoreForm.degree,
                            rolesArea: Session.get('area')
                        };

                        Meteor.call("insertSchInputScore", schInputScoreDoc, (err, result) => {
                            if (!err) {
                                vm.$message({
                                    duration: 1000,
                                    message: `Save Successfully!`,
                                    type: 'success'
                                });
                                vm.dialogAddSchInputScore = false;

                                vm.$refs["schInputScoreFormAdd"].resetFields();
                            } else {
                                vm.$message({
                                    duration: 1000,
                                    message: err.message,
                                    type: 'error'
                                });
                            }
                        })
                    }
                })

            },
            resetForm() {
                this.schInputScoreForm._id = "";
                if (this.$refs["schInputScoreFormAdd"]) {
                    this.$refs["schInputScoreFormAdd"].resetFields();
                }

                if (this.$refs["schInputScoreFormUpdate"]) {
                    this.$refs["schInputScoreFormUpdate"].resetFields();
                }

            }
        },
        created() {
            this.isSearching = true;
            this.fetchUser();
            this.fetchProgram();
            this.fetchSubject("");

        },
        computed: {
            langConfig() {
                let data = compoLang.filter(config => config.lang === this.langSession)[0]['inputScore'];
                return data;
            }
        }
    }
